package com.zenith.livinghistory.api.zenithlivinghistoryapi.services;import com.zenith.livinghistory.api.zenithlivinghistoryapi.security.codec.TokenCodec;import com.zenith.livinghistory.api.zenithlivinghistoryapi.security.token.AuthenticationToken;import com.zenith.livinghistory.api.zenithlivinghistoryapi.security.token.TokenFactory;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.Optional;@Servicepublic class AuthenticationService {    private TokenFactory tokenFactory;    private TokenCodec tokenCodec;    @Autowired    public AuthenticationService(TokenFactory tokenFactory, TokenCodec tokenCodec) {        this.tokenFactory = tokenFactory;        this.tokenCodec = tokenCodec;    }    public String generateTokenString(Long userId, String fullName) {        final AuthenticationToken token = this.tokenFactory.generateToken(userId, fullName);        return this.tokenCodec.encode(token);    }    public Optional<AuthenticationToken> decode(String tokenString) {        return this.tokenCodec.decode(tokenString);    }}